AccountsFuzzy identitypragma solidity ^0.4.21;interface IName &#123;    function name() external view returns (bytes32);&#125;contract FuzzyIdentityChallenge &#123;    bool public isComplete;    function authenticate() public &#123;        require(isSmarx(msg.sender));        require(isBadCode(msg.sender));        isComplete = true;    &#125;    function isSmarx(address addr) internal view returns (bool) &#123;        return IName(addr).name() == bytes32(&quot;smarx&quot;);    &#125;    function isBadCode(address _addr) internal pure returns (bool) &#123;        bytes20 addr = bytes20(_addr);        bytes20 id = hex&quot;000000000000000000000000000000000badc0de&quot;;        bytes20 mask = hex&quot;000000000000000000000000000000000fffffff&quot;;        for (uint256 i = 0; i &lt; 34; i++) &#123;            if (addr &amp; mask == id) &#123;                return true;            &#125;            mask &lt;&lt;= 4;            id &lt;&lt;= 4;        &#125;        return false;    &#125;&#125;

要求：

合约的name为smarx
合约地址里包含 badc0de

以太坊源码中生成合约地址的算法
func CreateAddress(b common.Address, nonce uint64) common.Address &#123;    data, _ := rlp.EncodeToBytes([]interface&#123;&#125;&#123;b, nonce&#125;) //对地址和nonce进行rlp编码    return common.BytesToAddress(Keccak256(data)[12:]) //利用keccak256算hash，后20个字节作为新地址&#125;

使用ethjs-account来生成地址
const rlp = require(&#x27;rlp&#x27;);const js = require(&#x27;_js-sha3@0.8.0@js-sha3&#x27;);const generate = require(&#x27;ethjs-account&#x27;).generate;seed=&#x27;892h@fs8sk^2hSFR*/8s8shfs.jk39hsoi@hohskd51D1Q8E1%^;DZ1-=.@WWRXNI()VF6/*Z%$C51D1QV*&lt;&gt;FE8RG!FI;&quot;./+-*!DQ39hsoi@hoFE1F5^7E%&amp;*QS&#x27;//生成地址所用的种子function fuzz()&#123;    for(var k=0;k&lt;50000;k++)&#123;        seed=seed+Math.random().toString(36).substring(12);//为避免重复，生成一定数目后对种子进行更新        for(var i=0;i&lt;1000;i++)&#123;            res=generate(seed);            for (var j=0;j&lt;10;j++)&#123;                encodedRlp = rlp.encode([res.address, j]);// 进行rlp编码                buf = js.keccak256(encodedRlp);                contractAddress =buf.slice(24).toString(&#x27;hex&#x27;);//取buffer第12个字节后面的部分作为地址                if(contractAddress.slice(33).match(&quot;badc0de&quot;))&#123;                    console.log(contractAddress);                    console.log(res);                    console.log(j);                    return;                &#125;            &#125;            //console.log(i);        &#125;    &#125;&#125;fuzz();

