def inverse_mod( a, m ):    &quot;&quot;&quot;Inverse of a mod m.&quot;&quot;&quot;    if a &lt; 0 or m &lt;= a: a = a % m    c, d = a, m    uc, vc, ud, vd = 1, 0, 0, 1    while c != 0:        q, c, d = divmod( d, c ) + ( c, )        uc, vc, ud, vd = ud - q*uc, vd - q*vc, uc, vc    assert d == 1    if ud &gt; 0: return ud    else: return ud + mdef derivate_privkey(p, r, s1, s2, z1, z2):    z = z1 - z2    s = s1 - s2    r_inv = inverse_mod(r, p)    s_inv = inverse_mod(s, p)    k = (z * s_inv) % p    d = (r_inv * (s1 * k - z1)) % p    return d, kz1 = 0x4f6a8370a435a27724bbc163419042d71b6dcbeb61c060cc6816cda93f57860cs1 = 0x2bbd9c2a6285c2b43e728b17bda36a81653dd5f4612a2e0aefdb48043c5108der = 0x69a726edfb4b802cbf267d5fd1dabcea39d3d7b4bf62b9eeaeba387606167166z2 = 0x350f3ee8007d817fbd7349c477507f923c4682b3e69bd1df5fbb93b39beb1e04s2 = 0x7724cedeb923f374bef4e05c97426a918123cc4fec7b07903839f12517e1b3c8p  = 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFEBAAEDCE6AF48A03BBFD25E8CD0364141print (&quot;privatekey:%x\n k:%x&quot; % derivate_privkey(p,r,s1,s2,z1,z2))



