铸币操作/** * @dev Creates `amount` tokens of token type `id`, and assigns them to `to`. * * Emits a &#123;TransferSingle&#125; event. * * Requirements: * * - `to` cannot be the zero address. * - If `to` refers to a smart contract, it must implement &#123;IERC1155Receiver-onERC1155Received&#125; and return the * acceptance magic value. */function _mint(    address to,    uint256 id,    uint256 amount,    bytes memory data) internal virtual &#123;    require(to != address(0), &quot;ERC1155: mint to the zero address&quot;);    address operator = _msgSender();    uint256[] memory ids = _asSingletonArray(id);    uint256[] memory amounts = _asSingletonArray(amount);    _beforeTokenTransfer(operator, address(0), to, ids, amounts, data);    _balances[id][to] += amount;    emit TransferSingle(operator, address(0), to, id, amount);    _afterTokenTransfer(operator, address(0), to, ids, amounts, data);    _doSafeTransferAcceptanceCheck(operator, address(0), to, id, amount, data);&#125;/** * @dev xref:ROOT:erc1155.adoc#batch-operations[Batched] version of &#123;_mint&#125;. * * Emits a &#123;TransferBatch&#125; event. * * Requirements: * * - `ids` and `amounts` must have the same length. * - If `to` refers to a smart contract, it must implement &#123;IERC1155Receiver-onERC1155BatchReceived&#125; and return the * acceptance magic value. */function _mintBatch(    address to,    uint256[] memory ids,    uint256[] memory amounts,    bytes memory data) internal virtual &#123;    require(to != address(0), &quot;ERC1155: mint to the zero address&quot;);    require(ids.length == amounts.length, &quot;ERC1155: ids and amounts length mismatch&quot;);    address operator = _msgSender();    _beforeTokenTransfer(operator, address(0), to, ids, amounts, data);    for (uint256 i = 0; i &lt; ids.length; i++) &#123;        _balances[ids[i]][to] += amounts[i];    &#125;    emit TransferBatch(operator, address(0), to, ids, amounts);    _afterTokenTransfer(operator, address(0), to, ids, amounts, data);