代币销毁/** * @dev Destroys `amount` tokens of token type `id` from `from` * * Emits a &#123;TransferSingle&#125; event. * * Requirements: * * - `from` cannot be the zero address. * - `from` must have at least `amount` tokens of token type `id`. */function _burn(    address from,    uint256 id,    uint256 amount) internal virtual &#123;    require(from != address(0), &quot;ERC1155: burn from the zero address&quot;);    address operator = _msgSender();    uint256[] memory ids = _asSingletonArray(id);    uint256[] memory amounts = _asSingletonArray(amount);    _beforeTokenTransfer(operator, from, address(0), ids, amounts, &quot;&quot;);    uint256 fromBalance = _balances[id][from];    require(fromBalance &gt;= amount, &quot;ERC1155: burn amount exceeds balance&quot;);    unchecked &#123;        _balances[id][from] = fromBalance - amount;    &#125;    emit TransferSingle(operator, from, address(0), id, amount);    _afterTokenTransfer(operator, from, address(0), ids, amounts, &quot;&quot;);&#125;/** * @dev xref:ROOT:erc1155.adoc#batch-operations[Batched] version of &#123;_burn&#125;. * * Emits a &#123;TransferBatch&#125; event. * * Requirements: * * - `ids` and `amounts` must have the same length. */function _burnBatch(    address from,    uint256[] memory ids,    uint256[] memory amounts) internal virtual &#123;    require(from != address(0), &quot;ERC1155: burn from the zero address&quot;);    require(ids.length == amounts.length, &quot;ERC1155: ids and amounts length mismatch&quot;);    address operator = _msgSender();    _beforeTokenTransfer(operator, from, address(0), ids, amounts, &quot;&quot;);    for (uint256 i = 0; i &lt; ids.length; i++) &#123;        uint256 id = ids[i];        uint256 amount = amounts[i];        uint256 fromBalance = _balances[id][from];        require(fromBalance &gt;= amount, &quot;ERC1155: burn amount exceeds balance&quot;);        unchecked &#123;            _balances[id][from] = fromBalance - amount;        &#125;    &#125;    emit Transfer